<?php/** * Плагин смарти для конструирования урла * * @param array $params * @return string */function smarty_function_Url (array $params){    $serviceRoute = App::serviceRoute();    $controllerAction = $params['to'];    $paramsRequired = isset($params['params']) ? $params['params'] : [] ;    $data = isset($params['data']) ? $params['data'] : [];    return $serviceRoute->createUrl($controllerAction, $paramsRequired, $data);}